package me.method17.exception.feature;

import me.method17.exception.utils.ClientUtil;
import me.method17.exception.utils.render.RenderUtil;
import net.minecraft.util.ResourceLocation;

import java.io.IOException;

public enum Category {
    COMBAT("Combat",""),
    MOVEMENT("Movement",""),
    WORLD("World",""),
    MISC("Misc",""),
    RENDER("Render",""),
    SETTINGS("Settings","");

    private final String name;
    private ResourceLocation icon;

    private Category(String name,String b64icon){
        this.name=name;
        //read icon resource from base64
        try {
            this.icon=ClientUtil.loadImageFromBase64(b64icon);
        } catch (IOException e) {
            e.printStackTrace();
        }

    }

    public String getName(){
        return name;
    }

    public void drawIconAt(int x, int y, int width, int height){
        RenderUtil.drawImage(icon,x,y,width,height);
    }
}
